import React from 'react';
import axios from 'axios';
import TodoList from './todos/TodoList';
import TodoListForm from './todos/TodoListForm';

import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
        todos: [],
        currentTodo: {},
     }
     this.updateCurrentTodo = this.updateCurrentTodo.bind(this);
}

componentDidMount() {
    const url = 'http://localhost:4000/todo';

    axios.get(url)

    .then((Response) => {
        this.setState({
            todos: Response.data
        })
    })

    .catch((error) => {
        console.log(error)
    });
};

updateCurrentTodo(item) {
    this.state({
        currentTodo: item,
    })
}

render() { 
    return ( 
        <div className="container">
            <div className="row">
            <div className="col s3">
                  <div className="col s3"><TodoListForm /></div>
            </div>
            <div className="col s4"><TodoList todos={this.state.todos}
              updateCurrentTodo={this.updateCurrentTodo} />
            </div>
            </div>
        </div>
     );
  }
}

export default App;
