import React from 'react';
import axios from 'axios';
import TodoListItem from './TodoListItem';
import TodoListForm from './TodoListForm';

class TodoList extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            todos: [],
            currentTodo: {},
         }
         this.updateCurrentTodo = this.updateCurrentTodo.bind(this);
    }

    componentDidMount() {
        const url = 'http://localhost:4000/todo';

        axios.get(url)
        .then((Response) => {
            this.setState({
                todos: Response.data
            })
        })
        .catch((error) => {
            console.log(error)
        });
    };

    updateCurrentTodo(item) {
        this.state({
            currentTodo: item,
        })
    }

    render() { 
        return ( 
            <div className="container-fluid">
                <div className="row">
                    <div className="col s3">todos={this.state.todos}
                    updateCurrentTodo={this.updateCurrentTodo} 
                    </div>
                </div>
            </div>
         );
    }
}

export default TodoList;


// TodoList's only prop is 'todos', which it is supposed to display
// <div className....
// It's going to simply take todos that it gets as a prop
// For each todo, it's going to display a TodoListItem component
