import React from 'react';
import axios from 'axios';

import './App.css';

class App_edit extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
        todos: []
     }
}

componentDidMount() {
    this.getAll();
}

getAll  = () => {
    axios.get('http://localhost:4000/todo')

    .then((res) => {
        this.setState({
            todos: res.data
        })
    })

    .catch((error) => {
        console.log(error)
    });
}    

todoEdit(id, e) {
    e.preventDefault();

    axios.put(`http://localhost:4000/todo/${id}`, {
       name: '',
       todoItem: ''
    })

    .then((res) => {
        console.log(res);
        this.getAll();
    })

    .catch((error) => {
        console.log(error);
        this.getAll();
    });
}

render() { 
    return ( 
        <div className="container">
            <div className="col s12 m4">
            <div className="row">
                <form className="col s12" onSubmit={this.todoEdit.bind(this)}>
                <div className="row">                    
                    <input id="name" ref="name" type="text"/>
                    <label htmlFor="name">Edit Name</label>
                </div>
                <div className="row">
                    <input id="todoItem" ref="todoItem" type="text"/>
                    <label htmlFor="todoItem">Edit Todo</label>
                </div>
                <button className="btn-floating material-icons btn-danger" type="submit" name="action">Update</button>
                </form>
            </div>
            </div>
        </div>
     );
  }
}

export default App_edit;
