import React from 'react';
import axios from 'axios';
import TodoList from './todos/TodoList';
import TodoListForm from './todos/TodoListForm';

import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
        todos: [],
     }
}

componentDidMount() {
    const url = 'http://localhost:4000/todo';

    axios.get(url)

    .then((Response) => {
        this.setState({
            todos: Response.data
        })
    })

    .catch((error) => {
        console.log(error)
    });
};

submitTodo(event) {
    event.preventDefault();

    axios.post('http://localhost:4000/todo', {
        name: this.refs.name.value,
        todoItem: this.refs.todoItem.value,
    })

    .then((response) => {
        console.log(response);
    })

    .catch((error) => {
        console.log(error);
    });
}

render() { 
    return ( 
        <div className="container">
            <h1 className="center card-panel teal lighten-2">ToDo Web App</h1>
            <div className="col">
            <div className="col s6">
                  <div className="col s12">
                  <div className="row">
                <div className="col s12 m4">
                    <div className="white">
                        <div className="row">
                            <form className="col s12" onSubmit={this.submitTodo.bind(this)}>
                                <div className="row">
                                    <div className="input-field col s6">
                                        <input id="name" ref="name" type="text" />
                                        <label htmlFor="name">Name</label>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="input-field col s12">
                                        <input id="todoItem" ref="todoItem" type="text" />
                                        <label htmlFor="todoItem">Add new Todo</label>
                                    </div>
                                </div>
                                <button className="btn-floating btn-medium center waves-effect waves-light" type="submit" name="action">+</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
                  </div>
            </div>
            <div className="col s3"><TodoList todos={this.state.todos}/></div>
            </div>
        </div>
     );
  }
}

export default App;
