import React from 'react';
import axios from 'axios';

import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
        todos: [],
        isToggleOn: true
     }
     this.changeHandler = this.changeHandler.bind(this);
     this.handleClick = this.handleClick.bind(this);
}

changeHandler = (e) => {
    this.setState({todos: e.target.value})
}

componentDidMount() {

    axios.get('http://localhost:4000/todo')

    .then((res) => {
        this.setState({
            todos: res.data
        })
    })

    .catch((error) => {
        console.log(error)
    });
};

todoSubmit(e) {
    e.preventDefault();
    console.log(this.state.todos)

    axios.post('http://localhost:4000/todo', {
        name: this.refs.name.value,
        todoItem: this.refs.todoItem.value
    })

    .then((res) => {
        console.log(res);
    })

    .catch((error) => {
        console.log(error);
    })

    this.setState({
        name: this.state.name,
        todoItem: this.state.todoItem
    })
}

todoDelete(id, e) {
    e.preventDefault();

    axios.delete(`http://localhost:4000/todo/${id}`)

    .then(res => {
        console.log(res);
        console.log(res.data);
    })

    .catch((error) => {
        console.log(error);
    })

    this.setState({
        todos: this.state.todos.filter(el => el._id !== id)
    })
}

  todoEdit(id, e) {
    e.preventDefault();

    axios.put(`http://localhost:4000/todo/${id}`, {
        name: '123',
        todoItem: '11'
    })

    .then((res) => {
        console.log(res);
        console.log(res.data);
    })

    .catch((error) => {
        console.log(error);
    });

    this.setState({
        todos: this.state.todos
    })
}

handleClick() {
    this.setState(state => ({
      isToggleOn: !state.isToggleOn
    }));
}

render() { 
    return ( 
        <div className="container">
            <h1 className="center card-panel teal lighten-2">ToDo Web App</h1>
            <div className="col s12 m4">
            <div className="row">
                <form className="col s12" onChange={this.changeHandler}>
                <div className="row">
                <div className="input-field col s6">
                    <input id="name" ref="name" type="text" />
                    <label htmlFor="name">Name</label>
                </div>
                </div>
                <div className="row">
                <div className="input-field col s12">
                <input id="todoItem" ref="todoItem" type="text" />
                    <label htmlFor="todoItem">Add new Todo</label>
                </div>
                </div>
                <button className="btn-floating material-icons btn-danger" type="submit" name="action" onSubmit={this.todoSubmit.bind(this)}>+</button>
                </form>
            </div>
            </div>
            <div className="col s3">
            <ul className="collection with-header">
                <li className="collection-header"><h5>Todos</h5></li>
                {this.state.todos.map((item) => (
                    <div className="collection-item" key={item._id}>
                        Name: {item.name} <br/> 
                        Todo: {item.todoItem} <br/> 
                        Date: {item.date} <br/>
                     <button className="btn-floating material-icons btn-danger" onClick={(e) => this.todoDelete(item._id, e)}>delete</button> &nbsp;
                     <button className="btn-floating material-icons btn-danger" onClick={(e) => this.todoEdit(item._id, e)}>create</button> &nbsp;
                     <button className="btn-floating material-icons btn-danger" onClick={this.handleClick}>{this.state.isToggleOn ? 'star_border' : 'star'}</button>
                    </div>
                ))}
            </ul>
            </div>
        </div>
     );
  }
}

export default App;
