import React from 'react';
import axios from 'axios';
import './App.css';

class App extends React.Component {
  constructor() {
    super();
    this.state = { 
        todos: [],
     }
}

componentDidMount() {
    const url = 'http://localhost:4000/todo';

    axios.get(url)

    .then((Response) => {
        this.setState({
            todos: Response.data
        })
    })

    .catch((error) => {
        console.log(error)
    });
};

submitTodo(event) {
    event.preventDefault();

    axios.post('http://localhost:4000/todo', {
        name: this.refs.name.value,
        todoItem: this.refs.todoItem.value,
    })

    .then((response) => {
        console.log(response);
    })

    .catch((error) => {
        console.log(error);
    });
}

todoDelete(id, e){
    axios.delete(`http://localhost:4000/todo/${id}`)
      .then(res => {
        console.log(res);
        console.log(res.data);

        const todos = this.state.todos.filter(item => item.id !== id);
        this.setState({ todos });
      })

  }

render() { 
    return ( 
        <div className="container">
            <h1 className="center card-panel teal lighten-2">ToDo Web App</h1>
            <div className="col s12 m4">
            <div className="row">
                <form className="col s12" onSubmit={this.submitTodo.bind(this)}>
                <div className="row">
                <div className="input-field col s6">
                    <input id="name" ref="name" type="text" />
                    <label htmlFor="name">Name</label>
                </div>
                </div>
                <div className="row">
                <div className="input-field col s12">
                <input id="todoItem" ref="todoItem" type="text" />
                    <label htmlFor="todoItem">Add new Todo</label>
                </div>
                </div>
                <button className="btn-floating btn-medium center waves-effect waves-light" type="submit" name="action">+</button>
                </form>
            </div>
            </div>
            <div className="col s3">
            <ul className="collection with-header">
                <li className="collection-header"><h5>Todos</h5></li>
                {this.state.todos.map((item) => (
                    <div className="collection-item" key={item._id}>Name: {item.name} <br/> Todo: {item.todoItem} <br/> Date: {item.date} <br/> 
                     <button className="btn-floating material-icons">grade</button>
                     <button className="btn-floating material-icons">create</button>
                     <button className="btn btn-danger" onDoubleClick={(e) => this.todoDelete(item.id, e)}>delete</button>
                    </div>
                ))}
            </ul>
            </div>
        </div>
     );
  }
}

export default App;
