import React from 'react';
import axios from 'axios';

class TodoListForm extends React.Component {

    submitTodo(event) {
        event.preventDefault();

        axios.post('http://localhost:4000/todo', {
            name: this.refs.name.value,
            todoItem: this.refs.todoItem.value,
        })

        .then((response) => {
            console.log(response);
        })

        .catch((error) => {
            console.log(error);
        });
    }

    onDelete(event) {
        event.preventDefault();

        axios.post('http://localhost:4000/todo/:TodoID', {
            name: this.refs.name.value,
            todoItem: this.refs.todoItem.value,
        })

        .then((response) => {
            console.log(response);
        })

        .catch((error) => {
            console.log(error);
        });
    }


    render() { 
        return ( 
            <div className="row">
                <div className="col s12 m4">
                    <div className="white">
                        <div className="row">
                            <form className="col s12" onSubmit={this.submitTodo.bind(this)}>
                                <div className="row">
                                    <div className="input-field col s6">
                                        <input id="name" ref="name" type="text" />
                                        <label htmlFor="name">Name</label>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="input-field col s12">
                                        <input id="todoItem" ref="todoItem" type="text" />
                                        <label htmlFor="todoItem">Add new Todo</label>
                                    </div>
                                </div>
                                <button className="btn-floating btn-medium center waves-effect waves-light" type="submit" name="action">+</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
         );
    }
}

export default TodoListForm;
