import React from 'react';
import axios from 'axios';
import TodoList from './todos/TodoList';
import TodoListForm from './todos/TodoListForm';

import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
        todos: [],
     }
}

componentDidMount() {
    const url = 'http://localhost:4000/todo';

    axios.get(url)

    .then((Response) => {
        this.setState({
            todos: Response.data
        })
    })

    .catch((error) => {
        console.log(error)
    });
};

render() { 
    return ( 
        <div className="container">
            <h1 className="center card-panel teal lighten-2">ToDo Web App</h1>
            <div className="col">
            <div className="col s6">
                  <div className="col s12"><TodoListForm/></div>
            </div>
            <div className="col s3"><TodoList todos={this.state.todos}/></div>
            </div>
        </div>
     );
  }
}

export default App;
